INSERT INTO ent_video_movie (id, name, cn_name, source_name, poster, size, medium, encode, audio_encode, definition, release_date, region, movie_type, language, cn_subtitles, imdb_url, imdb_score, douban_url, douban_score, length, discribe, create_by, create_time, update_by, update_time) VALUES (3, 'The Dark Knight Rises', '蝙蝠侠前传3/蝙蝠侠：黑暗骑士崛起', 'The Dark Knight Rises 2012 ULTRAHD Blu-ray 2160p HEVC DTS-HD MA 5.1-SGnb@CHDBits', 'http://localhost:88/video/movie/20190221150215674732.jpg', '92.53', 'UHD Blu-ray', 'H.265', 'DTS-HD', '4K', '2012-07-20', '', null, '', '1', 'http://www.imdb.com/title/tt1345836', '8.4', 'https://movie.douban.com/subject/3395373', '8.7', '165', '<p><font face="Lucida Grande">&nbsp; &nbsp; &nbsp; 检察官哈维·登特（艾伦·艾克哈特 Aaron Eckhart 饰）死后的八年间，哥谭市在戈登警长（加里·奥德曼 Gary Oldman 饰）的努力下有效地遏制了犯罪活动。而蝙蝠侠布鲁斯·韦恩（克里斯蒂安·贝尔 Christian Bale 饰）因为背负谋杀登特的罪名，被 警方通缉，在哥谭市销声匿迹。然而，这看似平静的状态被恐怖分子贝恩（汤姆·哈迪 Tom Hardy 饰）的出现打破，他利用猫女（安妮·海瑟薇 Anne Hathaway 饰）偷取布鲁斯的指纹，致使韦恩集团面临破财，布鲁斯被赶出董事会。为了保证韦恩集团投资的能源项目不被坏人利用制造核弹，布鲁斯帮助米兰达（玛丽昂·歌迪亚 Marion Cotillard 饰）成为董事会主席。贝恩接下来一连串精密设计的计划将哥谭市陷入孤绝之境，毁灭之日指日可待。布鲁斯只能再次肩负起拯救哥谭的重任，然而至亲的管家阿福辞职离开、猫女的背叛、贝恩的凶残手段，爱人米拉达的真实面目露出，都让他拯救哥谭的行动变得不可能……</font><br></p>', 1, '2019-02-21 15:02:17', 1, '2019-02-21 15:02:17');
INSERT INTO ent_video_movie (id, name, cn_name, source_name, poster, size, medium, encode, audio_encode, definition, release_date, region, movie_type, language, cn_subtitles, imdb_url, imdb_score, douban_url, douban_score, length, discribe, create_by, create_time, update_by, update_time) VALUES (2, 'The Dark Knight', '蝙蝠侠前传2/蝙蝠侠：黑暗骑士', 'The Dark Knight 2008 ULTRAHD Blu-ray 2160p HEVC DTS-HD MA 5.1-SGnb@CHDBits', 'http://localhost:88/video/movie/20190221143119393403.jpg', '70.45', 'UHD Blu-ray', 'H.265', 'DTS-HD', '4K', '2008-07-18', '', null, '', '1', 'http://www.imdb.com/title/tt0468569', '9.0', 'https://movie.douban.com/subject/1851857', '9.1', '152', '<p><font face="Lucida Grande">&nbsp; &nbsp; &nbsp; 从亲眼目睹父母被人杀死的阴影中走出来的“蝙蝠侠”，经历了成长之后，已经不再是那个桀骜不的孤单英雄了。在警官吉姆·戈登和检查官哈维·登特的通力帮助下，“蝙蝠侠”无后顾之忧地继续满世界的奔波，与日益增长起来的犯罪威胁做着永无休止的争斗，而他所在的高谭市，也是进展最为明显的地方，犯罪率以一种惊人的速度持续下降着，毕竟对方是能够上天入地的“蝙蝠侠”，不借两个胆子谁还敢造次呢？不过像高谭这种科技与污秽并存的城市，平静是不可能维持太久的，果不其然，新一轮的混乱很快就席卷了整个城市，人们再一被被恐慌所笼罩着，而声称愿意为这一切负责的，自然就是所有混乱的源头以及支配者--“小丑”了。</font></p><p><font face="Lucida Grande">&nbsp; &nbsp; &nbsp; 先不管“小丑”掀起一个又一个犯罪的狂潮的最终目的为何，他的企图都是邪恶的，所作所为更是早就危害到了高谭市民的正常生活……其中自然包括了“蝙蝠侠”身边几个非常重要的人，而他需要做的，就是将这股新的危机全部亲自用手捏得粉碎。然而在面对着这个有史以来最具针对性、最恶毒的对手时，“蝙蝠侠”却不得不从他的地下军械库里搬出每一件能够用得上的高科技武器，还得时刻纠结着为他曾经信仰的一切寻找答案。</font></p>', 1, '2019-02-21 14:32:11', 1, '2019-02-21 14:32:11');
