mapper.select:
SELECT
    count(1)
FROM
    ent_music_song

     and id = #{id} 
     and name = #{name} 
     and album_id = #{albumId}

SELECT song.id, song.name, song.album_id, album.name AS albumName, singer.id AS singerId, singer.name AS singerName, song.track_number, song.length, song.size, song.audio_type, song.lyrics, song.create_by, song.create_time, song.update_by, song.update_time FROM ent_music_song song JOIN ent_music_album album ON song.album_id = album.id JOIN ent_music_singer singer ON album.singer_id = singer.id
and id = #{id} and name = #{name} and album_id = #{albumId}
order by ${sort} ${order}
order by id
limit #{limit} offset #{offset}
