mapper.select:
SELECT
    COUNT(1)
FROM
    sys_dict

	 and id = #{id} 
	 and dict_name = #{dictName} 
	 and dict_value = #{dictValue} 
	 and dict_type = #{dictType} 
	 and description = #{description}

SELECT
    id,
    dict_name,
    dict_value,
    dict_type,
    description,
    sort,
    create_by,
    create_time,
    update_by,
    update_time
FROM
    sys_dict

	 and id = #{id} 
	 and dict_name = #{dictName} 
	 and dict_value = #{dictValue} 
	 and dict_type = #{dictType} 
	 and description = #{description} 



		order by ${sort} ${order}


		order by dict_type, sort



	limit #{limit} offset #{offset}
