mapper.select:
SELECT
    id,
    menu_name,
    parent_id,
    url,
    perms,
    type,
    icon,
    sort
FROM
    sys_menu

     and id = #{id} 
     and menu_name = #{menuName} 
     and parent_id = #{parentId} 
     and url = #{url} 
     and perms = #{perms} 
     and type = #{type} 
     and icon = #{icon} 



        order by ${sort} ${order}


        order by id



    limit #{limit} offset #{offset}
