mapper.select:
SELECT
    COUNT(1)
FROM
    sys_user

	 and id = #{id} 
	 and user_name = #{userName} 
	 and name = #{name} 
	 and password = #{password} 
	 and dept_id = to_number(#{deptId}, '9999999999999999999') 
	 and email = #{email} 
	 and mobile = #{mobile} 
	 and sex = #{sex} 
	 and birth = #{birth} 
	 and pic_id = #{picId} 
	 and address = #{address} 
	 and province = #{province} 
	 and city = #{city} 
	 and district = #{district} 
	 and status = #{status}

SELECT
    id,
    user_name,
    name,
    password,
    dept_id,
    email,
    mobile,
    sex,
    birth,
    pic_id,
    address,
    province,
    city,
    district,
    status,
    create_by,
    create_time,
    update_by,
    update_time
FROM
    sys_user

	 and id = #{id} 
	 and user_name = #{userName} 
	 and name = #{name} 
	 and password = #{password} 
	 and dept_id = to_number(#{deptId}, '9999999999999999999') 
	 and email = #{email} 
	 and mobile = #{mobile} 
	 and sex = #{sex} 
	 and birth = #{birth} 
	 and pic_id = #{picId} 
	 and address = #{address} 
	 and province = #{province} 
	 and city = #{city} 
	 and district = #{district} 
	 and status = #{status} 



		order by ${sort} ${order}


		order by id



	limit #{limit} offset #{offset}

mapper.update:
UPDATE sys_user
user_name = #{userName}, name = #{name}, password = #{password}, dept_id = #{deptId}, email = #{email}, mobile = #{mobile}, sex = #{sex}, birth = #{birth}, pic_id = #{picId}, address = #{address}, province = #{province}, city = #{city}, district = #{district}, status = #{status}, update_by = #{updateBy}, update_time = CURRENT_TIMESTAMP
WHERE id = #{id}
